import { A_Z, Z_A, WEIGHT_MAX, WEIGHT_MIN } from "../actions/index";

const initialState = {
  dogs: [],
  temperaments: [],
  filtered: 'LOADING...',
  dogDetail: [],
  currentPage: 1,
  breedsClean: [],
};

function rootReducer(state = initialState, action) {
  switch (action.type) {
    case 'GET_DOGS':
      return {
        ...state,
        dogs: action.payload,
        filtered: action.payload,
      };
    case 'GET_TEMPERAMENTS':
      return {
        ...state,
        temperaments: action.payload,
      };
   

    case 'GET_DETAIL':
      return {
        ...state,
        dogDetail: action.payload,
      };

    case 'GET_BY_NAME':
        return {
          ...state,
          dogs: action.payload,
        }

    case 'GET_BY_TEMPERAMENTS':
      return {
        ...state,
       temperaments: action.payload,
        }

    

        case 'FILTER_BREED':
          let allBreeds = [...state.breedsClean]
          let aux;
          console.log(allBreeds)
          if(action.payload === 'All Breeds') return { ...state, dogs: allBreeds}
          if(action.payload === 'Breeds') {
              aux = allBreeds.filter(e=> Number(e.id))               
          } 
          if(action.payload === 'New Breeds') {
              aux = allBreeds.filter(e=> !Number(e.id))
          }
          if(action.payload === 'Weight -10') {
              aux = allBreeds.filter(e=> e.weightMin > 10)
          }

          console.log(aux)
          return{
              ...state,
              dogs: aux
          }
      case 'FILTER_TEMPERAMENT':
        let allTemperaments = [...state.breedsClean]
        //if(action.payload === 'All Temperaments') return {...state, breeds: state.breeds}
        let aux2 = action.payload === 'All Temperaments' ? allTemperaments : allTemperaments.filter(el => el.temperament.includes(action.payload))
        console.log(aux2)                
        return {
            ...state,
            dogs: aux2
        } 
        case 'ORDER_BY':
          let orderAz = [...state.dogs]
          orderAz = orderAz.sort((a, b) => {
              switch(action.payload){
                  case A_Z:
                      if(a.name < b.name) {
                          return -1;
                      } else return 1
                  case Z_A:
                      if(a.name > b.name) {
                          return -1;
                      } else return 1
                  case WEIGHT_MAX:
                      if(a.weightMax > b.weightMax) {
                          return -1
                      } else return 1
                  case WEIGHT_MIN:
                      if(a.weightMin < b.weightMin) {
                          return -1
                      } else return 1
                  default: return 0;
                  }  
          })
          return {
            ...state,
            breeds: orderAz

        } 
  
      case 'LOADING':
        return{
            ...state,
            loading: true
        }  
      case 'CLEAN':
        return {
          ...state,
          dogDetail: {},
        }

    default:
      return state;
  }
}

export default rootReducer;
