import { useDispatch, useSelector } from "react-redux";
import {
  orderBy,
  filterTemperament,
  getTemperaments,
  filterBy,
} from "../store/actions";
import { useState, useEffect } from "react";
import styles from "../styles/filters.module.css";

export default function Filters({ setCurrentPage }) {
  const dispatch = useDispatch();
  const [state, setState] = useState("");
  const temperaments = useSelector((state) => state.temperaments);

  //ordeno los temperamentos de a-z
  const temperamentsOrder = temperaments.sort((a, b) => {
    if (a.name > b.name) {
      return 1;
    }
    if (a.name < b.name) {
      return -1;
    }
    return 0;
  });

  //filtro para los perros que vienen de la api o creados
  function handleFilter(e) {
    e.preventDefault();
    dispatch(filterBy(e.target.value));
    dispatch(orderBy(orderBy, orderDirection));
  }

  useEffect(() => {
    dispatch(getTemperaments());
  }, [dispatch]);
  //filtro pa los temperamentos
  function onFilterTemperament(e) {
    e.preventDefault();
    dispatch(filterTemperament(e.target.value));
    dispatch(orderBy(orderBy, orderDirection));
  }
  //filtro para ordenar de la a-z y por peso
  const [orderBy, setOrderBy] = useState("name");
  const [orderDirection, setOrderDirection] = useState("asc");

  return (
    <>
      <div className={styles.filtersContainer}>
      <select
    className={styles.filters}
    value={orderBy}
    onChange={(e) => setOrderBy(e.target.value)}
  >
    <option value="name">Nombre</option>
    <option value="weight">Peso</option>
  </select>
  <select
    className={styles.filters}
    value={orderDirection}
    onChange={(e) => setOrderDirection(e.target.value)}
  >
    <option value="asc">Ascendente</option>
    <option value="desc">Descendente</option>
  </select>
        <div>
          <select className={styles.filters} onChange={onFilterTemperament}>
            <option
              value="All Temperaments"
              key="All Temperaments"
              disabled
              selected
            >
              Ordenar por Temperamentos
            </option>
            {temperamentsOrder.map((el, index) => (
              <option value={el.name} key={index}>
                {el.name}
              </option>
            ))}
          </select>
        </div>
        <div>
          <select className={styles.filters} onChange={(e) => handleFilter(e)}>
            <option value="" disabled selected>
              Ordenar por Creados/Api
            </option>
            <option value="All">Api / Creados</option>
            <option value="api">api</option>
            <option value="Created">Created</option>
          </select>
        </div>
      </div>
    </>
  );
}
