import { A_Z, Z_A, WEIGHT_MAX, WEIGHT_MIN } from "./actions";

const initialState = {
  dogs: [],
  temperaments: [],
  filtered: 'LOADING...',
  dogDetail: [],
  currentPage: 1,
  breedsClean: [],
};

function rootReducer(state = initialState, action) {
  switch (action.type) {
    case 'GET_DOGS':
      return {
        ...state,
        dogs: action.payload,
        filtered: action.payload,
      };
    case 'GET_TEMPERAMENTS':
      return {
        ...state,
        temperaments: action.payload,
      };
   

    case 'GET_DETAIL':
      return {
        ...state,
        dogDetail: action.payload,
      };

    case 'GET_BY_NAME':
        return {
          ...state,
          dogs: action.payload,
        }

    case 'GET_BY_TEMPERAMENTS':
      return {
        ...state,
       temperaments: action.payload,
        }

    

        case 'FILTER_BY':
        const alldogs = state.dogs;
        /*  console.log(action.payload); */
        const filter2 =
          action.payload === "Created"
            ? alldogs.filter((ele) => ele.createdInDb)
            : alldogs.filter((ele) => !ele.createdInDb);
        return {
          ...state,
          dogs:
            action.payload === "All"
              ? alldogs
              : filter2.length
              ? filter2
              : [],
        }; 
        case "FILTER_TEMPERAMENT":
          const allDogs = state.dogs;
          console.log(allDogs)
          const temperamentsFiltered = allDogs.filter((dog) =>
            // dog.temperament &&
            dog.temperaments.map((temp) => temp).includes(action.payload)
          );
    
          return {
            ...state,
            dogs: temperamentsFiltered,
          };

        case 'ORDER_BY':
          let orderAz = [...state.dogs]
          orderAz = orderAz.sort((a, b) => {
              switch(action.payload){
                  case A_Z:
                      if(a.name < b.name) {
                          return -1;
                      } else return 1
                  case Z_A:
                      if(a.name > b.name) {
                          return -1;
                      } else return 1
                  case WEIGHT_MAX:
                      if(a.weightMax > b.weightMax) {
                          return -1
                      } else return 1
                  case WEIGHT_MIN:
                      if(a.weightMin < b.weightMin) {
                          return -1
                      } else return 1
                  default: return 0;
                  }  
          })
          return {
            ...state,
            breeds: orderAz

        } 
  
      case 'LOADING':
        return{
            ...state,
            loading: true
        }  
      case 'CLEAN':
        return {
          ...state,
          dogDetail: {},
        }

    default:
      return state;
  }
}

export default rootReducer;
